import React, { Component } from 'react';
import CanvasJSReact from '../../assets/canvasjs.react';
import { Card, CardContent } from '@material-ui/core';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

var dps = [{x: 1995, y: 10}, {x: 1996, y: 13}, {x: 1997, y: 18}, {x: 1998, y: 20}, {x: 1999, y: 17},{x: 2000, y: 10}, {x: 2001, y: 13}, {x: 2002, y: 18}, {x: 2003, y: 20}, {x: 2004, y: 17}];   //dataPoints.
var xVal = 2005;
var yVal = 15;
var updateInterval = 3000;

class DynamicLineChart extends Component {
	constructor() {
		super();
		this.updateChart = this.updateChart.bind(this);
	}
	componentDidMount() {
		setInterval(this.updateChart, updateInterval);
	}
	updateChart() {
		yVal = yVal +  Math.round(5 + Math.random() *(-5-5));
		dps.push({x: xVal,y: yVal});
		xVal++;
		if (dps.length >  10 ) {
			dps.shift();
		}
		this.chart.render();
	}
	render() {
		const options = {
			subtitles: [{
				text: "Intel Core i7 980X @ 3.33GHz",
				fontSize:18
			}],
			axisX: {
				title: "Date"
			},
			legend: {
				cursor:"pointer",
				horizontalAlign: "bottom",
				fontSize: 12,
				fontColor: "dimGrey",
			},
			data: [{
				type: "area",
				markerSize: 15,
				color:'#ffcc5e',
				lineColor:"#988c85",
				lineThickness: 4,
				dataPoints : dps,
				showInLegend: true,
				name: "Date",
			}]
		}
		
		return (
			<Card className="card">
				<CardContent >
					<CanvasJSChart options = {options} 
					onRef={ref => this.chart = ref}
					/>
				</CardContent>
			</Card>
			
			
		);
	}
}

export default DynamicLineChart;
