import React, { Component } from 'react';
import CanvasJSReact from '../../assets/canvasjs.react';
import { Card, CardContent } from '@material-ui/core';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

var dps = [{x: 1985, y: 10}, {x: 1986, y: 13}, {x: 1987, y: 18}, {x: 1988, y: 20}, {x: 1989, y: 17},{x: 1990, y: 10}, {x: 1991, y: 13}, {x: 1992, y: 18}, {x: 1993, y: 20}, {x: 1994, y: 17}];   //dataPoints.
var xVal = 1995;
var yVal = 15;
var updateInterval = 3000;

class DynamicLineChart extends Component {
	constructor() {
		super();
		this.updateChart = this.updateChart.bind(this);
	}
	componentDidMount() {
		setInterval(this.updateChart, updateInterval);
	}
	updateChart() {
		yVal = yVal +  Math.round(5 + Math.random() *(-5-5));
		dps.push({x: xVal,y: yVal});
		xVal++;
		if (dps.length >  10 ) {
			dps.shift();
		}
		this.chart.render();
	}
	render() {
		const options = {
			subtitles: [{
				text: "Revenue for the financial Year",
				fontSize:18
			}],
			axisX: {
				title: "Years",
				maximum: 2020
			},
			axisY: {
				title: "Revenue (%)",
				suffix: "%",
			},
			legend: {
				cursor:"pointer",
				horizontalAlign: "bottom",
				fontSize: 12,
				fontColor: "dimGrey",
			},
			data: [{
				type: "area",
				yValueFormatString: "#,###'%'",
				indexLabel: "{y}",
				markerSize: 15,
				color:'#ffcc5e',
				lineColor:"#988c85",
				lineThickness: 4,
				dataPoints : dps,
				showInLegend: true,
				name: "Year",
			}]
		}
		
		return (
			<Card className="card">
				<CardContent >
					<CanvasJSChart options = {options} 
					onRef={ref => this.chart = ref}
					/>
				</CardContent>
			</Card>
			
			
		);
	}
}

export default DynamicLineChart;
